INSERT INTO creature_text values (27316,10,0,"May the power of Light compel you!",12,7,100,0,0,0,"Inquisitor Hallard",26813);
DELETE FROM creature_text WHERE entry=27316 AND groupid=11 AND id=0;
INSERT INTO creature_text values (27316,11,0,"I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?",12,7,100,396,0,0,"Inquisitor Hallard",26824);
DELETE FROM creature_text WHERE entry=27316 AND groupid=12 AND id=0;
INSERT INTO creature_text values (27316,12,0,"RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!",12,7,100,6,0,0,"Inquisitor Hallard",26826);
DELETE FROM creature_text WHERE entry=27316 AND groupid=13 AND id=0;
INSERT INTO creature_text values (27316,13,0,"What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.",12,7,100,6,0,0,"Inquisitor Hallard",26829);
DELETE FROM creature_text WHERE entry=27316 AND groupid=14 AND id=0;
