DELETE FROM creature_text WHERE entry=27316 AND groupid=15 AND id=0;
INSERT INTO creature_text values (27316,15,0,"Return to Halford with the information that the good mayor was kind enough to submit, $n. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"",12,7,100,1,0,0,"Inquisitor Hallard",26833);
DELETE FROM creature_text WHERE entry=27316 AND groupid=16 AND id=0;
