INSERT INTO creature_text values (27383,0,0,"I was... once like you. Mortal. Soft. Only flesh and bone. WEAK AND TIMID!",14,0,100,0,0,0,"Thel zan the Duskbringer",27157);
DELETE FROM creature_text WHERE entry=27383 AND groupid=1 AND id=0;
