DELETE FROM creature_text WHERE entry=27383 AND groupid=2 AND id=0;
INSERT INTO creature_text values (27383,2,0,"\"Serve me in life and I will promise to rend the weakness from your soul. To erase it from existence!\" To be chosen... blessed by the Lich King in such a way.",14,0,100,0,0,0,"Thel zan the Duskbringer",27160);
DELETE FROM creature_text WHERE entry=27383 AND groupid=3 AND id=0;
INSERT INTO creature_text values (27383,3,0,"So serve him I did... Were it not for me the glorious dread citadel of Naxxramas may never have returned to Northrend.",14,0,100,0,0,0,"Thel zan the Duskbringer",27161);
DELETE FROM creature_text WHERE entry=27383 AND groupid=4 AND id=0;
INSERT INTO creature_text values (27383,4,0,"But enough talk... You have fought hard and come from the distant reaches of this world to face the Duskbringer. I shall not disappoint...",14,0,100,0,0,0,"Thel zan the Duskbringer",27162);
DELETE FROM creature_text WHERE entry=27383 AND groupid=5 AND id=0;
