DELETE FROM creature_text WHERE entry=27439 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27439,1,0,"Did you think that I could not see through your flimsy disguise, $N?",12,0,100,6,0,0,"High Abbot Landgren",26631);
DELETE FROM creature_text WHERE entry=27439 AND groupid=2 AND id=0;
INSERT INTO creature_text values (27439,2,0,"There is much that you do not understand, $R. The Master sees all.",12,0,100,1,0,0,"High Abbot Landgren",26632);
DELETE FROM creature_text WHERE entry=27439 AND groupid=3 AND id=0;
INSERT INTO creature_text values (27439,3,0,"He told me that you would come for me. I won\'t die by your hand, though. I have seen what you have done to my compatriots.",12,0,100,25,0,0,"High Abbot Landgren",26633);
DELETE FROM creature_text WHERE entry=27439 AND groupid=5 AND id=0;
INSERT INTO creature_text values (27439,5,0,"AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................",12,0,100,0,0,2943,"High Abbot Landgren",26636);
DELETE FROM creature_text WHERE entry=27476 AND groupid=0 AND id=0;
INSERT INTO creature_text values (27476,0,0,"I apologize, emissary, but the prince is away on an errand. Wha brings you to this desolate place?",12,0,100,1,3000,12719,"Valonforth 0  - Q: The Truth Shall Set Us Free",26709);
DELETE FROM creature_text WHERE entry=27476 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27476,1,0,"We\'re to just pick up and leave?",12,0,100,6,1500,12720,"Valonforth 1  - Q: The Truth Shall Set Us Free",26711);
DELETE FROM creature_text WHERE entry=27476 AND groupid=2 AND id=0;
INSERT INTO creature_text values (27476,2,0,"To hell with the undead! We\'ll cut our way through the woods, men!",12,0,100,22,3000,12721,"Valonforth 2  - Q: The Truth Shall Set Us Free",26713);
DELETE FROM creature_text WHERE entry=27476 AND groupid=3 AND id=0;
INSERT INTO creature_text values (27476,3,0,"Well, milord, your father had our troops recalled at Lord Uther\'s request.",12,0,100,1,3000,12722,"Valonforth 3 - Q: The Truth Shall Set Us Free",26715);
DELETE FROM creature_text WHERE entry=27492 AND groupid=0 AND id=0;
INSERT INTO creature_text values (27492,0,0,"By royal edict, you men are to return to Lordaeron immediately. Lord Uther has convinced the king to recall this expedition.",12,0,100,25,3500,12723,"Alliance Emissary 0 - Q: The Truth Shall Set Us Free",26710);
DELETE FROM creature_text WHERE entry=27492 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27492,1,0,"That\'s correct. My men report that the roads from here to the shore are held by the undead. You\'ll need to find an alternate route back to your ships.",12,0,100,378,4000,12724,"Alliance Emissary 1 - Q: The Truth Shall Set Us Free",26712);
DELETE FROM creature_text WHERE entry=27577 AND groupid=10 AND id=0;
INSERT INTO creature_text values (27577,10,0,"How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!",12,0,100,1,0,0,"Mayor Godfrey",26831);
DELETE FROM creature_text WHERE entry=27577 AND groupid=11 AND id=0;
INSERT INTO creature_text values (27577,11,0,"You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!",12,0,100,1,0,0,"Mayor Godfrey",26832);
DELETE FROM creature_text WHERE entry=27577 AND groupid=8 AND id=0;
INSERT INTO creature_text values (27577,8,0,"NOOOO!!! I tell you! I tell you everything!",12,0,100,39,0,0,"Mayor Godfrey",26827);
DELETE FROM creature_text WHERE entry=27577 AND groupid=9 AND id=0;
INSERT INTO creature_text values (27577,9,0,"Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...",12,0,100,1,0,0,"Mayor Godfrey",26828);
DELETE FROM creature_text WHERE entry=2768 AND groupid=8 AND id=0;
INSERT INTO creature_text values (2768,8,0,"%s hands the glowing goggles over to Doctor Draxlegauge.",16,0,100,0,0,0,"Professor Phizzlethorpe - EMOTE_PROGRESS_8",889);
DELETE FROM creature_text WHERE entry=2768 AND groupid=9 AND id=0;
INSERT INTO creature_text values (2768,9,0,"Doctor Draxlegauge will give you further instructions, $n. Many thanks for your help!",12,0,100,0,0,0,"Professor Phizzlethorpe - SAY_PROGRESS_9",890);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=1;
INSERT INTO creature_text values (27713,0,1,"Is this the end? If we stop this bastard now, is victory assured at the Wrathgate?",12,0,100,0,0,0,"7th Legion Elite",27146);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=2;
INSERT INTO creature_text values (27713,0,2,"Let it be known that it has been an honor and a privilege fighting alongside all of you.",12,0,100,0,0,0,"7th Legion Elite",27148);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=3;
INSERT INTO creature_text values (27713,0,3,"Death comes for us all eventually, but sometimes it comes a little faster than anticipated. Today we call that blind heroics... Tomorrow someone may call it stupidity.",12,0,100,0,0,0,"7th Legion Elite",27150);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=4;
INSERT INTO creature_text values (27713,0,4,"Come on... Let\'s get this over with!",12,0,100,0,0,0,"7th Legion Elite",27149);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=5;
INSERT INTO creature_text values (27713,0,5,"It looks like a gateway to hell!",12,0,100,0,0,0,"7th Legion Elite",27144);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=6;
INSERT INTO creature_text values (27713,0,6,"I\'m getting a little worried. Where is this hero that is supposed to lead us into battle?",12,0,100,0,0,0,"7th Legion Elite",27147);
DELETE FROM creature_text WHERE entry=27713 AND groupid=0 AND id=7;
INSERT INTO creature_text values (27713,0,7,"If I don\'t make it back to Wintergarde, let my family know that I did my best and that I love them...",12,0,100,0,0,0,"7th Legion Elite",27145);
DELETE FROM creature_text WHERE entry=27713 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27713,1,0,"It\'s the Highlord!",12,0,100,0,0,0,"7th Legion Elite",27178);
DELETE FROM creature_text WHERE entry=27784 AND groupid=10 AND id=0;
INSERT INTO creature_text values (27784,10,0,"Pay attention. This is Instructor Razuvious, Kel\'Thuzad\'s appointed trainer of all death knights. It is said that his own technique is so potent, only a disciple of his could possibly withstand his might.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27060);
DELETE FROM creature_text WHERE entry=27784 AND groupid=11 AND id=0;
INSERT INTO creature_text values (27784,11,0,"Gothik, the Harvester. A master of necromancy and conjuration, Gothik is said to be able to beckon forth legions of the undead at a moment\'s notice. It is with his guidance that even the weakest of Death Knights can raise the dead.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27061);
DELETE FROM creature_text WHERE entry=27784 AND groupid=12 AND id=0;
INSERT INTO creature_text values (27784,12,0,"The most powerful death knights within Naxxramas are the four horsemen. They represent the pinnacle of Kel\'Thuzad\'s guard. The first, Thane Korth\'azz, holds the power of flame. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27062);
DELETE FROM creature_text WHERE entry=27784 AND groupid=13 AND id=0;
INSERT INTO creature_text values (27784,13,0,"The next, Lady Blaumeux, calls forth the power of shadows, drawing the souls of the living into the voracious embrace.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27063);
DELETE FROM creature_text WHERE entry=27784 AND groupid=14 AND id=0;
INSERT INTO creature_text values (27784,14,0,"Sir Zeliek, a paladin in life, so strong in his faith, that even in undeath, the power of the light still heeds his call, smiting his foes in battle.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27064);
DELETE FROM creature_text WHERE entry=27784 AND groupid=15 AND id=0;
INSERT INTO creature_text values (27784,15,0,"The final death knight, Lord Mograine, was the finest warrior that the death knights had ever seen. However, after the events in Azeroth, he vanished without a trace. It is yet to be seen who Kel\'Thuzad has appointed in his place.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27065);
DELETE FROM creature_text WHERE entry=27784 AND groupid=16 AND id=0;
INSERT INTO creature_text values (27784,16,0,"Behold, Patchwerk. When word of his existence first reached the ears of the Brotherhood, none believed the tales of an abomination with such immense speed and strength. Fewer still believed it when he fell the first time...",12,0,100,0,0,0,"Commander Eligor Dawnbringer",27074);
DELETE FROM creature_text WHERE entry=27784 AND groupid=17 AND id=0;
INSERT INTO creature_text values (27784,17,0,"Grobbulus, the first of what was to be an army of flesh giants. It carried the plague slime of Naxxramas within its body, injecting the living ooze into the bodies of its foe. The recipient of this foul injection would usually flee to his allies, bringing them down with him.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27075);
DELETE FROM creature_text WHERE entry=27784 AND groupid=18 AND id=0;
INSERT INTO creature_text values (27784,18,0,"Gluth, the foul plague-dog of Naxxramas, was said to have an appetite so voracious that even the living were not enough to satisfy his hunger. Feugen was said to feed him an army of zombies daily, recycling the remains of undead too weak to use in combat.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27077);
DELETE FROM creature_text WHERE entry=27784 AND groupid=19 AND id=0;
INSERT INTO creature_text values (27784,19,0,"Thaddius, built from the flesh of women and children, it is said that their souls are fused together - eternally bound within that foul prison of flesh. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27078);
DELETE FROM creature_text WHERE entry=27784 AND groupid=20 AND id=0;
INSERT INTO creature_text values (27784,20,0,"Anub\'Rekhan, one of the finest Spider Lords of Azjol\'Nerub, was brought under the control of the Scourge when he and his former commander, Anub\'arak, were finally defeated by Lich King many years ago. It is said that he was the most loyal of Anub\'arak\'s commanders.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27066);
DELETE FROM creature_text WHERE entry=27784 AND groupid=21 AND id=0;
INSERT INTO creature_text values (27784,21,0,"The Grand Widow Faerlina, a botanist in life, breeder and caretaker of arachnids in death, she overseers the spider wing, developing the most potent of poisons for the Lich King. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27070);
DELETE FROM creature_text WHERE entry=27784 AND groupid=22 AND id=0;
INSERT INTO creature_text values (27784,22,0,"Maexxna, born deep within the mountains of Northrend many ages ago, she was captured within the dread citadel Naxxramas, where she gives birth to her brood, feeding them the corpses of all who dare venture too deeply into Naxxramas.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27073);
DELETE FROM creature_text WHERE entry=27784 AND groupid=23 AND id=0;
INSERT INTO creature_text values (27784,23,0,"Behold, Noth the Plaguebringer. Responsible for the creation of the process that distills the souls of the living and places them within the cold cage of undeath, Noth was observed to be refining this process even now. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27055);
DELETE FROM creature_text WHERE entry=27784 AND groupid=24 AND id=0;
INSERT INTO creature_text values (27784,24,0,"Observe, Heigan the Unclean. The mastermind behind the plague cauldrons that turned the wilderness of Azeroth into the Plaguelands. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27056);
DELETE FROM creature_text WHERE entry=27784 AND groupid=25 AND id=0;
INSERT INTO creature_text values (27784,25,0,"It is said that Heigan has rigged the very walls and floors of Naxxramas itself with a vast array of traps, which he can trigger at will.",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27057);
DELETE FROM creature_text WHERE entry=27784 AND groupid=26 AND id=0;
INSERT INTO creature_text values (27784,26,0,"Loatheb. The hideous result of fusing the living plague of the Plaguelands with the bog beasts of Azeroth, Loatheb is said to control the power of healing itself. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27058);
DELETE FROM creature_text WHERE entry=27784 AND groupid=8 AND id=0;
INSERT INTO creature_text values (27784,8,0,"Kel\'thuzad has survived trials that would have long since shattered the souls of even the greatest of the Brotherhood. He readily gave his life at Arthas\'s hand, later to be brought back - reborn in the power of the Sunwell. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27081);
DELETE FROM creature_text WHERE entry=27784 AND groupid=9 AND id=0;
INSERT INTO creature_text values (27784,9,0,"In the Plaguelands, Kel\'thuzad was again defeated by the agents of the Argent Dawn. However, his phylactery was delivered to a false agent of the Brotherhood. We have yet to discover who stole his phylactery or why. ",12,7,100,0,0,0,"Commander Eligor Dawnbringer",27082);
DELETE FROM creature_text WHERE entry=27843 AND groupid=0 AND id=0;
INSERT INTO creature_text values (27843,0,0,"I\'ll flush her out, $N! You just be ready for her when she comes in!",12,0,100,1,0,0,"Wyrmbait",27120);
DELETE FROM creature_text WHERE entry=27844 AND groupid=0 AND id=0;
INSERT INTO creature_text values (27844,0,0,"FIRE! FIRE! BRING HER DOWN!",14,0,100,0,0,0,"Legion Commander Tyralion",27123);
DELETE FROM creature_text WHERE entry=27857 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27857,1,0,"It is this phylactery, Thel\'zan\'s phylactery - that is the key to this victory!",12,0,100,0,0,0,"Legion Commander Yorik",27156);
DELETE FROM creature_text WHERE entry=27857 AND groupid=2 AND id=0;
INSERT INTO creature_text values (27857,2,0,"LEGION, STEADY YOURSELVES!",12,0,100,0,0,0,"Legion Commander Yorik",27158);
DELETE FROM creature_text WHERE entry=27858 AND groupid=0 AND id=0;
INSERT INTO creature_text values (27858,0,0,"Honor, courage, compassion and justice! Those were once the virtues you fought for as a cleric of the Argent Dawn, Thel\'zan.",14,0,100,0,0,0,"Highlord Bolvar Fordragon",27173);
DELETE FROM creature_text WHERE entry=27858 AND groupid=1 AND id=0;
INSERT INTO creature_text values (27858,1,0,"You dedicated your whole life to fighting the Scourge. You saw what they did to our beloved Lordaeron. The Lich King took everything from you!",14,0,100,0,0,0,"Highlord Bolvar Fordragon",27174);
DELETE FROM creature_text WHERE entry=27858 AND groupid=2 AND id=0;
INSERT INTO creature_text values (27858,2,0,"So how, then, did Father Inigo Montoy, bastion of virtue for the Argent Dawn, become the embodiment of that which he hated most?",12,0,100,0,0,0,"Highlord Bolvar Fordragon",27175);
DELETE FROM creature_text WHERE entry=27858 AND groupid=3 AND id=0;
INSERT INTO creature_text values (27858,3,0,"Now, be free and join the battle! Let us end this together!",12,0,100,0,0,0,"Highlord Bolvar Fordragon",27182);
DELETE FROM creature_text WHERE entry=27858 AND groupid=4 AND id=0;
INSERT INTO creature_text values (27858,4,0,"Victory for the Alliance!",14,0,100,0,0,0,"Highlord Bolvar Fordragon",27180);
DELETE FROM creature_text WHERE entry=27858 AND groupid=5 AND id=0;
INSERT INTO creature_text values (27858,5,0,"You have saved Wintergarde! With Thel\'zan\'s filth washed away, the 7th Legion will easily clean up the remaining Scourge. When you are done here, report to Angrathar!",12,0,100,0,0,0,"Highlord Bolvar Fordragon",27181);
DELETE FROM creature_text WHERE entry=27899 AND groupid=10 AND id=0;
