INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(24703,5,0,"Honor is meaningless to a dead man. A warrior may throw himself into battle for honor\'s sake, but a chieftain is responsible for far more. We will follow Sage Aeire\'s plan and continue to defend our village.",12,0,100,1,0,0,24803,"Chieftain Wintergale");
