INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25162,20,0,"Alright, quiet down. We have a lot to do and very little time to do it in, people.",12,0,100,0,0,0,24210,"Drill Sergeant Bahduum");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25162,21,0,"We\'re here as support for the so-called \'adventurers\' that the brass has called in. We\'ll help them hold the key points on the island, and keep the heat off so that they can do their jobs.",12,0,100,0,0,0,24214,"Drill Sergeant Bahduum");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25162,22,0,"If none of you ladies have any questions, you can get further instructions and assignments from your squad leaders. That\'s it. Good luck!",12,0,100,0,0,0,24215,"Drill Sergeant Bahduum");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25162,23,0,"That\'s all I have to say for now. Stay focused, boys and girls.",12,0,100,0,0,0,24200,"Drill Sergeant Bahduum");
