INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25166,14,0,"Your luck has run its course!",14,0,100,0,0,12493,25517,"eredar - YELL_BERSERK");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25197,0,0,"There, $n, now you will be able to speak with, and understand, the Winterfin murlocs.",12,0,100,1,0,0,24428,"King Mrgl-Mrgl");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,0,0,"%s grunts.",16,1,100,0,0,0,24491,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,1,0,"Shipping lanes... supplies... You bore me to death! We need nothing more than the warrior spirit of the Horde, Saurfang! Now that we are firmly entrenched in this frozen wasteland, nothing shall stop us!",12,1,100,1,0,0,24492,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,2,0,"Propose? I will show you what I propose!",12,1,100,6,0,0,24494,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,3,0,"There... Now we now have a shipping lane.",12,1,100,1,0,0,24495,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,4,0,"And just for good measure...",12,1,100,5,0,0,24496,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,5,0,"Do not speak to me of consequences, old one.",12,1,100,25,0,0,24501,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,6,0,"But surely you cannot think that those children were born into innocence? They would have grown up and taken arms against us!",12,1,100,5,0,0,24512,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25237,7,0,"How have you managed to survive for so long, Saurfang? Not fallen victim to your own memories?",12,1,100,6,0,0,24518,"Garrosh Hellscream");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25248,4,0,"Finally!  This charade is over... Arthas give me strength!",12,0,100,0,0,0,25245,"hidden cultist SAY_HIDDEN_CULTIST_2");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25248,5,0,"You don\'t know who you\'re messing with, $c!  Death beckons!",12,0,100,0,0,0,25246,"hidden cultist SAY_HIDDEN_CULTIST_3");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25248,6,0,"I suppose this is it, then?  I won\'t go down quietly!",12,0,100,0,0,0,25244,"hidden cultist SAY_HIDDEN_CULTIST_4");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25250,2,0,"Ugh!  My head won\'t stop spinning...",12,7,100,0,0,0,25847,"thassarian SAY_ARLOS_2");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25250,3,0,"Your offer will be carefully considered, harbinger.  In the meantime we will make use of your delegation in an advisory role.",12,7,100,1,0,0,24790,"General Arlos");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25251,4,0,"Thassarian!  You\'re alive!",14,0,100,0,0,0,25842,"thassarian SAY_LERYSSA_1");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25251,5,0,"Don\'t leave me again!  You want to fight for your country, but they don\'t even want you!  They sent you here to die!",12,0,100,0,0,0,25848,"thassarian SAY_LERYSSA_3");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25251,6,0,"I know that look in your eye... I\'m not going to be able to talk you out of this.  If you die on me again...",12,0,100,0,0,0,25849,"thassarian SAY_LERYSSA_4");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,0,0,"We are surrounded... Our enemies press in from all sides, young Hellscream.",12,1,100,1,0,0,24486,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,1,0,"The Scourge descends like locust from the north.",12,1,100,25,0,0,24487,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,2,0,"The Alliance holds the only secure shipping lane in this region, and even that is at risk of being lost to those dreaded mists.",12,1,100,25,0,0,24488,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,3,0,"Our only viable port for resupply is held by the Forsaken on the other side of this blasted continent!",12,1,100,1,0,0,24489,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,4,0,"Anything our zeppelins cannot haul must be brought in by ship and travel the length of Northrend to reach us.",12,1,100,1,0,0,24490,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,5,0,"Siege engines, ammunition, heavy armor... How do you propose to shatter the walls of Icecrown without those?",12,1,100,1,0,0,24493,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,6,0,"So the prodigal son has spoken!",12,1,100,5,0,0,24997,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,7,0,"Your father\'s blood runs strong in you, Hellscream. Impatient as always... Impatient and reckless.",12,1,100,1,0,0,24498,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,8,0,"You rush headlong into all-out war without a thought of the consequences.",12,1,100,1,0,0,24500,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,9,0,"I drank of the same blood your father did, Garrosh. Mannoroth\'s cursed venom pumped through my veins as well.",12,1,100,1,0,0,24502,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,10,0,"I drove my weapons into the bodies and minds of my enemies.",12,1,100,1,0,0,24503,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,11,0,"And while Grom died a glorious death - freeing us all from the blood curse - he could not wipe away the terrible memory of our past.",12,1,100,1,0,0,24504,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,12,0,"His act could not erase the horrors we committed.",12,1,100,274,0,0,24505,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,13,0,"%s pauses.",16,1,100,0,0,0,24506,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,14,0,"The winter after the curse was lifted, hundreds of veteran orcs like me were lost to despair.",12,1,100,1,0,0,24507,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,15,0,"Our minds were finally free, yes... Free to relive all of the unthinkable acts that we had performed under the Legions\' influence.",12,1,100,273,0,0,24508,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,16,0,"%s nods.",16,1,100,273,0,0,24509,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,17,0,"I think it was the sounds of the draenei children that unnerved most of them... You never forget...",12,1,100,1,0,0,24510,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,18,0,"Have you ever been to Jaggedswine Farm? When the swine are of age for the slaughter... It\'s that sound. The sound of the swine being killed... It resonates the loudest. Those are hard times for us older veterans.",12,1,100,6,0,0,24511,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,19,0,"%s shakes his head.",16,1,100,274,0,0,24513,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,20,0,"I am not speaking solely of the children of our enemies...",12,1,100,1,0,0,24515,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,22,0,"I won\'t let you take us down that dark path again, young Hellscream. I\'ll kill you myself before that day comes...",12,1,100,274,0,0,24516,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,23,0,"I don\'t eat pork...",12,1,100,274,0,0,24519,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25256,24,0,"%s spits.",16,1,100,0,0,0,24520,"High Overlord Saurfang");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25285,0,0,"Give the word, general.  I will have two regiments at your disposal in a month\'s time.",12,7,100,0,0,0,24787,"Harbinger Vurenn");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25285,1,0,"Very well, general.  Should you change your mind, my people will be more than willing to provide military assistance.",12,7,100,0,0,0,24788,"Harbinger Vurenn");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25301,0,0,"Our troops, general, consist mostly of villagers and peasants.  Good men, but not quite rid of the prejudices and superstitions of their upbringing.  They\'re not ready to fight alongside our more exotic allies.",12,7,100,1,0,0,24789,"Counselor Talbot");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25315,9,0,"Another step toward destruction!",14,0,100,0,0,12501,25549,"KJ - SAY_KJ_SLAY1");
