INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25315,19,0,"Kil\'jaeden begins to channel dark energy.",41,0,100,0,0,0,25705,"KJ - EMOTE_KJ_DARKNESS");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(25319,11,0,"Strike now heroes, while he is weakened! Vanquish the Deceiver!",14,0,100,0,0,12449,25822,"KJ - SAY_KALECGOS_ENCOURAGE");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
