INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29032,77,0,"A pact was made, $g brother:sister;! We vowed vengeance against the Lich King! For what he had done to us! We battled the Scourge as Forsaken, pushing them back into the Plaguelands and freeing Tirisifal! You and I were champions of the Forsaken!",12,0,100,0,0,0,29398,"special_surprise SAY_EXEC_THINK_1");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
