INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29068,77,0,"A pact was made, $g brother:sister;! We vowed vengeance against the Lich King! For what he had done to us! We battled the Scourge as Forsaken, pushing them back into the Plaguelands and freeing Tirisifal! You and I were champions of the Forsaken!",12,0,100,0,0,0,29398,"special_surprise SAY_EXEC_THINK_1");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29068,78,0,"You must remember the splendor of life, $g brother:sister;. You were a champion of the kaldorei once! This isn\'t you!",12,0,100,0,0,0,29328,"special_surprise SAY_EXEC_THINK_2");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29068,79,0,"Think, $n. Think back. Try and remember the majestic halls of Silvermoon City, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the sin\'dorei once! This isn\'t you.",12,0,100,0,0,0,29374,"special_surprise SAY_EXEC_THINK_3");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29068,80,0,"Think, $n. Think back. Try and remember the proud mountains of Argus, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the draenei once! This isn\'t you.",12,0,100,0,0,0,29362,"special_surprise SAY_EXEC_THINK_4");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29068,81,0,"Think, $n. Think back. Try and remember the snow capped mountains o\' Dun Morogh! Ye were born there, $g lad:lass;. Remember the splendor o\' life, $n! Ye were a champion o\' the dwarves once! This isn\'t ye!",12,0,100,0,0,0,29340,"special_surprise SAY_EXEC_THINK_5");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
