INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29175,86,0,"We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives - our lives!",12,0,100,0,0,14594,29843,"Highlord Tirion Fordring");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(29175,87,0,"The Lich King must answer for what he has done, and must not be allowed to cause further destruction in our world.",12,0,100,0,0,14596,29846,"Highlord Tirion Fordring");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
