INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30897,5,0,"Additional background is relevant to your query: following global combat between Aesir and Vanir-",14,0,100,0,0,13762,30606,"brann SAY_EVENT_END_15_MARN");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30897,6,0,"Unknown. Data suggests that impetus for global combat originated with prime designate Loken, who neutralized all remaining Aesir and Vanir, affecting termination of conflict. Prime designate Loken then initiated stasis of several seed races, including earthen, giants and vrykul, at designated holding facilities.",14,0,100,0,0,13763,30607,"brann SAY_EVENT_END_17_MARN");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30898,4,0,"Security breach in progress. Analysis of historical archives transferred to lower-priority queue. Countermeasures engaged.",14,0,100,0,0,13756,29575,"brann SAY_EVENT_A_2_KADD");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30898,5,0,"Accessing... Creators arrived to extirpate symbiotic infection. Assessment revealed that Old God infestation had grown malignant. Excising parasites would result in loss of host-",14,0,100,0,0,13757,30603,"brann SAY_EVENT_END_09_KADD");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30898,6,0,"Correct. Creators neutralized parasitic threat and contained it within the host. Forge of Wills and other systems were instituted to create new earthen. Safeguards were implemented, and protectors were appointed.",14,0,100,0,0,13758,30604,"brann SAY_EVENT_END_11_KADD");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30898,7,0,"Designations: Aesir and Vanir. Or in the common nomenclature, storm and earth giants. Sentinel Loken designated supreme. Dragon Aspects appointed to monitor evolution on Azeroth.",14,0,100,0,0,13759,30605,"brann SAY_EVENT_END_13_KADD");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30899,8,0,"Warning: life form pattern not recognized. Archival processing terminated.  Continued interference will result in targeted response.",14,0,100,0,0,13765,29561,"brann SAY_EVENT_INTRO_3_ABED");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
