INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30899,12,0,"Accessing... In the early stages of it\'s development cycle, Azeroth suffered infection by parasitic necrophotic symbiotes.",14,0,100,0,0,13771,30612,"brann SAY_EVENT_END_05_ABED");
INSERT INTO creature_text (entry,groupid,id,text,type,language,probability,emote,duration,sound,BroadcastTextID,comment) value 
(30899,13,0,"Designation: Old Gods. Old Gods rendered all systems, including earthen, defenseless in order to facilitate assimilation. This matrix destabilization has been termed \"the Curse of Flesh\". Effects of destabilization increased over time.",14,0,100,0,0,13772,30613,"brann SAY_EVENT_END_07_ABED");
